# Set up GUI.

root=tk.Tk()
text=tk.Text(root)
root.wm_title("Simple Binary Editor")


scrollbar = tk.Scrollbar(root)
scrollbar.config(command=text.yview)

text.grid(row=0, column=0, columnspan=5)
scrollbar.grid(row=0, column=5, sticky="ns")

menubar = tk.Menu(root)

file_menu = tk.Menu(menubar, tearoff=0)
file_menu.add_command(label="New", command=new_file)
file_menu.add_command(label="Open", command=open_file)
file_menu.add_command(label="Save as...", command=save_as)
menubar.add_cascade(label="File", menu=file_menu)

example_menu = tk.Menu(menubar, tearoff=0)
example_menu.add_command(label="Text", command=text_example)
example_menu.add_command(label="Excutable: x64 Intel Assembly 'mov rax, 0'", command=x64exe_example)
example_menu.add_command(label="Image: tiny bitmap .bmp image 24bit colour", command=bmp_example)
example_menu.add_command(label="Wave file example, signed 16bit signed PCM encoding", command=wav_example)

menubar.add_cascade(label="Examples", menu=example_menu)

readme_menu = tk.Menu(menubar, tearoff=0)
readme_menu.add_command(label="About", command=about)
readme_menu.add_command(label="Licence", command=licence)

menubar.add_cascade(label="Readme", menu=readme_menu)


